<script>$(document).ready(function(){	$(".accordion").click(function(){		var obj= $(this).next();		var objid = $(obj).attr('id');		$('.panel').removeClass('show');		if($(obj).hasClass('show')){			$(obj).removeClass('show');		}else{			$(obj).addClass('show');		}					});	$('#payment_confirm').click(function(){		var comment =$('#payment_order_comment').val().trim();		var payment_type =$('#payment_type').val().trim();		if(comment.length>0 ){				$.post("<?php echo base_url()?>index.php/customer/addPayment",{comment:comment,payment_type:payment_type,'<?php echo $this->security->get_csrf_token_name(); ?>' : '<?php echo $this->security->get_csrf_hash(); ?>'},function(data){					if(data){						$('.payment-error').hide();						$('.confirm').show();						$('#panel1').hide();					}else{						$('.payment-error-ul').html(data);						$('.payment-error').show();					}				});				$('.payment-error').hide();		}else{				$('.payment-error-ul').html('<li>Please Enter Comment </li>');				$('.payment-error').show();		}	});	$('#address_save').click(function(){		var address_type = $('input[name=address_type]:checked').val();			if(address_type=='1'){			var existing_address = $('#existing_address').val();						if(existing_address.length>0){								$.post("<?php echo base_url()?>index.php/customer/addAddress",{address_type:address_type,existing_address_id:existing_address,'<?php echo $this->security->get_csrf_token_name(); ?>' : '<?php echo $this->security->get_csrf_hash(); ?>'},function(data){					if(data){						$('.address-error').hide();						$('.payment').show();						$('#panel1').hide();					}else{						$('.address-error-ul').html(data);						$('.address-error').show();					}				});			}else{								$('.address-error-ul').html('<li>Please Select Address </li>');				$('.address-error').show();			}					}else{			var error=0;			var msg='';			var address=$('#address').val().trim();			var name=$('#name').val().trim();			var country=$('#country').val().trim();			var state=$('#state').val().trim();			var city=$('#city').val().trim();			var pincode=$('#pincode').val().trim();			var mobile=$('#mobile').val().trim();			var email=$('#email').val().trim();						if(name.length<=0){				error=1;				msg+='<li>Please Enter Name</li>';			}			if(address.length<=0){				error=1;				msg+='<li>Please Enter Address</li>';			}			if(country.length<=0){				error=1;				msg+='<li>Please Enter Country</li>';			}			if(state.length<=0){				error=1;				msg+='<li>Please Enter State</li>';			}			if(city.length<=0){				error=1;				msg+='<li>Please Enter City</li>';			}			if(pincode.length<=0){				error=1;				msg+='<li>Please Enter Pincode</li>';			}			if(mobile.length<=0){				error=1;				msg+='<li>Please Enter Mobile</li>';			}			if(email.length<=0){				error=1;				msg+='<li>Please Enter Email</li>';			}			if(error=='1'){				$('.address-error-ul').html(msg);				$('.address-error').show();			}else{				$.post("<?php echo base_url()?>index.php/customer/addAddress",					{					address_type:address_type,					name:name,					address:address,					country:country,					state:state,					city:city,					pincode:pincode,					email:email,					mobile:mobile,					'<?php echo $this->security->get_csrf_token_name(); ?>' : '<?php echo $this->security->get_csrf_hash(); ?>'					},					function(data){												if(data){							$('.address-error').hide();							$('.payment').show();							$('#panel1').hide();						}else{							$('.address-error-ul').html(data);							$('.address-error').show();						}				});							}					}			});});</script> <script type="text/javascript">       	 (function() {	var a = document.createElement("script");             a.type =	"text/javascript";             a.async = !0;            	a.src = "http://d3